
// Tooltip
$('#paramsCopy, #signStrCopy, #signCopy, #paymenturlCopy, #bufCopy, #responseCopy, #verifyCopy, #remarksCopy').tooltip({title: "Copy to clipboard", trigger: "hover"}); 

// Card Hover Action
$( ".card" ).hover(
    function() {
        $(this).addClass('shadow-lg').css('cursor', 'pointer'); 
    }, function() {
        $(this).removeClass('shadow-lg');
    }
);

// Copy Button Action
var param = document.getElementById("paramsCopy");
var signStr = document.getElementById("signStrCopy");
var sign = document.getElementById("signCopy");
var paymenturl = document.getElementById("paymenturlCopy");
var buf = document.getElementById("bufCopy");
var response = document.getElementById("responseCopy");
var verify = document.getElementById("verifyCopy");
var remark = document.getElementById("remarksCopy");
      
if (param) {
    document.getElementById("paramsCopy").addEventListener("click", function() {
        copyToClipboard("paramsCopy");
    });
}
if (signStr) {
    document.getElementById("signStrCopy").addEventListener("click", function() {
        copyToClipboard("signStrCopy");
    });
}
if (sign) {
    document.getElementById("signCopy").addEventListener("click", function() {
        copyToClipboard("signCopy");
    });
}
if (paymenturl) {
    document.getElementById("paymenturlCopy").addEventListener("click", function() {
        copyToClipboard("paymenturlCopy");
    });
}
if (buf) {
    document.getElementById("bufCopy").addEventListener("click", function() {
        copyToClipboard("bufCopy");
    });
}
if (response) {
    document.getElementById("responseCopy").addEventListener("click", function() {
        copyToClipboard("responseCopy");
    });
}
if (verify) {
    document.getElementById("verifyCopy").addEventListener("click", function() {
        copyToClipboard("verifyCopy");
    });
}
if (remark) {
    document.getElementById("remarksCopy").addEventListener("click", function() {
        copyToClipboard("remarksCopy");
    });
}
        